INSERT INTO `tour` (`tourCode`, `tourName`, `tourImage`, `startDate`, `price`, `description`, `employeeCode`, `vehicleCode`, `tourPackageCode`, `endDate`) VALUES
(1, 'Tour Đà Nẵng', '../../asset/images/tours/cau-vang-da-nang.png', '2024-01-10 08:00:00', 5000000, 'Du lịch Đà Nẵng Tết Nguyên Đán - Ba Nà - Hội An từ Sài Gòn 2025.  Tết Nguyên Đán là thời điểm tuyệt vời để khám phá Đà Nẵng, một trong những thành phố đáng sống nhất Việt Nam. Chuyến du lịch từ Sài Gòn đến Đà Nẵng trong dịp Tết không chỉ mang đến cho bạn cơ hội tận hưởng không khí lễ hội mà còn khám phá vẻ đẹp của Bà Nà Hills và Hội An.', 1, 1, 1, '2024-01-15 18:00:00'),
(2, 'Tour Nha Trang', '../../asset/images/tours/lagi.jpg', '2024-02-20 08:00:00', 6000000, 'Du lịch Nha Trang - Tết Âm lịch là dịp tuyệt vời để bạn và gia đình có một chuyến du lịch kết hợp nghỉ dưỡng tại thành phố biển Nha Trang xinh đẹp. Nha Trang không chỉ nổi tiếng với biển xanh, cát trắng mà còn là điểm đến lý tưởng với nhiều địa danh hấp dẫn. Hành trình khám phá Nha Trang dịp Tết sẽ đưa bạn qua những địa điểm nổi bật như khu vui chơi VinWonders và di tích Tháp Bà Ponagar, kết hợp với trải nghiệm ẩm thực độc đáo và tham quan cảnh đẹp thành phố biển.', 2, 2, 2, '2024-02-25 18:00:00'),
(3, 'Tour Sapa', '../../asset/images/tours/cot-co-lung-cu-ha-giang.png', '2024-01-12 08:00:00', 7000000, 'Du lịch Tây Bắc mùa Xuân - Hà Nội - Mộc Châu - Cầu Kính Bạch Long - Điện Biên - Sapa từ Sài Gòn 2025. Hành trình tuyệt vời khám phá Tây Bắc mùa xuân: ngắm hoa đào nở rộ tại Mộc Châu, thử thách bản thân trên Cầu Kính Bạch Long kỳ vĩ, trải nghiệm không khí lịch sử tại Điện Biên, và đắm mình trong cảnh sắc mờ sương của Sapa. Mỗi điểm đến là một câu chuyện riêng, tạo nên chuyến đi tràn đầy cảm xúc và kỷ niệm khó quên.', 3, 1, 1, '2024-01-17 18:00:00'),
(4, 'Tour Huế', '../../asset/images/tours/chua-nui-mot-con-dao.jpg', '2024-02-22 08:00:00', 4000000, 'Du lịch Đà Nẵng mùa Thu - Hội An - Huế 3N2Đ từ Sài Gòn 2024. Du lịch Đà Nẵng được mệnh danh là ‘’thành phố đáng đến’’ với dòng sông Hàn thơ mộng với cây cầu Rồng biểu tượng của Thành phố biển du lịch Đà Nẵng - nơi mà quý khách có thể cảm nhận được sự pha trộn giữa khí hậu miền Bắc, miền Nam. Ngoài ra Đà Nẵng còn sở hữu nhiều danh lam thắng cảnh làm say lòng người như: Bà Nà Hills, Bán Đảo Sơn Trà, Ngũ Hành Sơn, Đà Nẵng, phố cổ Hội An….', 4, 2, 2, '2024-02-27 18:00:00');
